mapper.select:
select count(*) 
from User
where email=#{email} and password=#{password}

select uid, email, name, phone, login_type as loginType
from User
where email=#{email}

select login_type
from User
where email=#{email}

select count(*)
from User
where email = #{email} and phone = #{phone}

mapper.update:
update User 
set	password=#{password} 
where email=#{email}

mapper.select:
select uid, email, name, phone, login_type as loginType
from User
where uid=#{uid}
